/* eslint-disable @typescript-eslint/naming-convention */
import { Table } from 'apache-arrow'
import { CosmographConfig } from './config'

/**
 * Represents the data operated by the Cosmograph.
 *
 * The data is stored in the Apache Arrow `Table` format, which is a fast and efficient way to store and process large amounts of data.
 *
 * For more information on the `Table` format, see the [Apache Arrow documentation](https://arrow.apache.org/docs/js/classes/Arrow_dom.Table.html).
 */
export type CosmographData = Table

export const RequiredPointsConfigKeys = {
  Points: 'points',
  PointId: 'pointIdBy',
  PointIndex: 'pointIndexBy',
} as const

export const BasePointsConfigKeys = {
  PointColor: 'pointColorBy',
  PointColorFn: 'pointColorByFn',
  PointSize: 'pointSizeBy',
  PointSizeRange: 'pointSizeRange',
  PointSizeFn: 'pointSizeByFn',
  PointLabel: 'pointLabelBy',
  PointLabelWeight: 'pointLabelWeightBy',
  PointX: 'pointXBy',
  PointY: 'pointYBy',
  PointCluster: 'pointClusterBy',
  PointClusterStrength: 'pointClusterStrengthBy',
  PointIncludeColumns: 'pointIncludeColumns',
} as const

export const RequiredLinksConfigKeys = {
  Links: 'links',
  LinkSource: 'linkSourceBy',
  LinkSourceIndex: 'linkSourceIndexBy',
  LinkTarget: 'linkTargetBy',
  LinkTargetIndex: 'linkTargetIndexBy',
} as const

export const BaseLinksConfigKeys = {
  LinkColor: 'linkColorBy',
  LinkColorFn: 'linkColorByFn',
  LinkWidth: 'linkWidthBy',
  LinkWidthRange: 'linkWidthRange',
  LinkWidthFn: 'linkWidthByFn',
  LinkArrow: 'linkArrowBy',
  LinkArrowFn: 'linkArrowByFn',
  LinkStrength: 'linkStrengthBy',
  LinkStrengthFn: 'linkStrengthByFn',
  LinkStrengthRange: 'linkStrengthRange',
  LinkIncludeColumns: 'linkIncludeColumns',
} as const

export const BaseClustersConfigKeys = {
  ClusterPositions: 'clusterPositions',
} as const

export interface CosmographPointsConfig {
  /** Input data for the points.
   *
   * {@link CosmographInputData} Accepts `File | string | Table | Uint8Array | ArrayBuffer | Record<string, unknown>[]` */
  [RequiredPointsConfigKeys.Points]?: CosmographInputData;

  /** Unique identifier column for each point. Required for mapping links to points correctly. */
  [RequiredPointsConfigKeys.PointId]?: string;

  /** Numeric index column for each point. Used for efficient lookups and should be a sequential integer starting from 0. */
  [RequiredPointsConfigKeys.PointIndex]?: string;

  /** The column name for the point color. If provided, points will be colored based on the values in this column, which should be either a color `string` or an array of numeric `[r, g, b, a]` values.
   */
  [BasePointsConfigKeys.PointColor]?: string;

  /** Specifies the function that will be used to generate the color for each point based on the value in the `pointColorBy` column. It takes a point record as input and its index, and should return a color `string` or an array of `[r, g, b, a]` values.
   *
   * Works only when `pointColorBy` is provided. Overrides the values in `pointColorBy` column by processing them (in this case the values in the `pointColorBy` column can be of any type, not just colors).
   *
   * @param {any} value - The value from the `PointColor` column.
   * @param {number} index - The index of the point.
   * @returns {string | [number, number, number, number]} The color as a `string` or an array of `[r, g, b, a]` value to be applied to the point.
   */
  [BasePointsConfigKeys.PointColorFn]?: AccessorFn<string | [number, number, number, number]>;

  /**
   * The column name for the point size.
   * If provided, points will be sized based on the values in this column, which should be numeric values.
   */
  [BasePointsConfigKeys.PointSize]?: string;

  /**
   * Defines the range for automatic point size scaling. Takes [min, max] values in pixels.
   *
   * When `pointSizeBy` column contains numeric values, they will be automatically remapped
   * to fit within this range to prevent oversized points if no `pointSizeByFn` provided.
   *
   * Note: Only works when `pointSizeBy` column is provided and contains numeric values and when `pointSizeByFn` is not set.
   *
   * @default [2, 9]
   */
  [BasePointsConfigKeys.PointSizeRange]?: [number, number];

  /** Specifies the function that will be used to generate the size for each point based on the value in the `pointSizeBy` column.
   * It takes a point record as input and its index, and should return a numeric value.
   *
   * Works only when `pointSizeBy` is provided.
   * Overrides the values in the `pointSizeBy` column by processing them (in this case the values in the `pointSizeBy` column can be of any type, not just numbers).
   *
   * If `null`, use `pointSizeBy` values as-is. If `undefined`, uses an auto-generated function that scales `pointSizeBy` values to the range `[2, 9]`
   *
   * @param {any} value - The value from the `pointSizeBy` column.
   * @param {number} index - The index of the point.
   * @returns {number} The numeric size value to be applied to the point.
   */
  [BasePointsConfigKeys.PointSizeFn]?: AccessorFn<number> | null;

  /**
   * The column name for the point clusters.
   * If provided, points will be clustered by the values in this columns. The column should contain strictly sequential cluster indexes starting from 0.
   *
   * @example
   * Correct: `[0, 1, 0, 2, undefined, 1]` - Use sequential indexes to sync with `clusterPositions` that alignes coordinates with the indexes of the clusters.
   * Incorrect usage: `[0, 1, 0, 5, undefined, 1]` - has a gap between 1 and 5, not guaranted to be aligned with `clusterPositions`.
   *
   * Points 0 and 2 belong to cluster 0, points 1 and 5 to cluster 1, and point 3 to cluster 2.
   * Point 4 is unclustered.
   * @note Clusters without specified positions via `setClusterPositions` will be positioned at their centermass by default.
   */
  [BasePointsConfigKeys.PointCluster]?: string;

  /**
   * The `pointClusterStrengthBy` column defines how strongly each point is attracted to its assigned cluster during the simulation.
   *
   * The column should contain numeric values where higher values (closer to 1.0) = stronger attraction, point stays closer to its cluster, Lower values (closer to 0.0) = weaker attraction, point can move more freely away from its cluster
   *
   * Has effect only when `pointClusterBy` is provided.
   */
  [BasePointsConfigKeys.PointClusterStrength]?: string;

  /** The column name for the point label. */
  [BasePointsConfigKeys.PointLabel]?: string;

  /** The column name for the point label weight. Accepts float values from 0 to 1.
   * Higher weights make labels more likely to be shown.
   *
   * Note: Make sure the targeted point labels are visible to have effect (won't work well with `showDynamicLabels` set to `true` because they are randomly selected): check targeted points will be displayed as the the top ones with `showTopLabels: true` or set point ids explicitly with `showLabelsFor`.
   */
  [BasePointsConfigKeys.PointLabelWeight]?: string;

  /** The column name for the point's x-coordinate.
   * If provided with `pointYBy`, points will be positioned based on the values from `pointXBy` and `pointYBy` columns. */
  [BasePointsConfigKeys.PointX]?: string;

  /** The column name for the point's y-coordinate.
   * If provided with `pointXBy`, points will be positioned based on the values from `pointXBy` and `pointYBy` columns. */
  [BasePointsConfigKeys.PointY]?: string;

  /** An array of additional column names to include in the point data.
   *
   * These columns will be available on the point objects but not used by Cosmograph directly, can be used as accessors for Cosmograph comopnents.
   * Useful for storing additional information about the points.
   */
  [BasePointsConfigKeys.PointIncludeColumns]?: string[];
}

export interface CosmographLinksConfig {
  /** The input data for the links.
   *
   * {@link CosmographInputData} Accepts `File | string | Table | Uint8Array | ArrayBuffer | Record<string, unknown>[]`
   */
  [RequiredLinksConfigKeys.Links]?: CosmographInputData;

  /** The column name for the **source** point of each link. This should match the `pointIdBy` values in the points data. */
  [RequiredLinksConfigKeys.LinkSource]?: string;

  /** The column name for the index of the **source** point of each link.
   * This is used for efficient lookups and should match the `pointIndexBy` values in the points data.
   */
  [RequiredLinksConfigKeys.LinkSourceIndex]?: string;

  /** The column name for the **target** point of each link. This should match the `pointIdBy` values in the points data. */
  [RequiredLinksConfigKeys.LinkTarget]?: string;

  /** The column name for the index of the **target** point of each link.
   * This is used for efficient lookups and should match the `pointIndexBy` values in the points data.
   */
  [RequiredLinksConfigKeys.LinkTargetIndex]?: string;

  /** The column name for the link color.
   *
   * If provided, links will be colored based on the values in this column, which should be either a color `string` or an array of numeric `[r, g, b, a]` values.
   */
  [BaseLinksConfigKeys.LinkColor]?: string;

  /** Specifies the function that will be used to generate the color for each link based on the value in the `linkColorBy` column.
   * It takes a link record as input and its index, and should return a color `string` or an array of `[r, g, b, a]` values.
   *
   * Works only when `linkColorBy` is provided. Overrides the values in `linkColorBy` column by processing them (in this case the values in the `linkColorBy` column can be of any type, not just colors).
   *
   * @param {any} value - The value from the `LinkColor` column.
   * @param {number} index - The index of the link.
   * @returns {string | [number, number, number, number]} The color as a `string` or an array of `[r, g, b, a]` value to be applied to the link.
   */
  [BaseLinksConfigKeys.LinkColorFn]?: AccessorFn<string | [number, number, number, number]>;

  /** The column name for the link width.
   *
   * If provided, links will have their widths set based on the values in this column, which should be numeric values.
   */
  [BaseLinksConfigKeys.LinkWidth]?: string;

  /**
   * Defines the range for automatic link width scaling. Takes [min, max] values in pixels.
   *
   * When `linkWidthBy` column contains numeric values, they will be automatically remapped to fit within this range to prevent oversized links if no `linkWidthByFn` provided.
   *
   * Note: Only works when `linkWidthBy` column is provided and contains numeric values and when `linkWidthByFn` is not set.
   *
   * @default [1, 9]
   */
  [BaseLinksConfigKeys.LinkWidthRange]?: [number, number];


  /** Specifies the function that will be used to generate the width for each link based on the value in the `linkWidthBy` column.
   * It takes a link record as input and its index, and should return a numeric value.
   *
   * Works only when `linkWidthBy` is provided. Overrides the values in the `linkWidthBy` column by processing them (in this case the values in the `linkWidthBy` column can be of any type, not just numbers).
   *
   * @param {any} value - The value from the `LinkWidth` column.
   * @param {number} index - The index of the link.
   * @returns {number} The numeric width value to be applied to the link.
   */
  [BaseLinksConfigKeys.LinkWidthFn]?: AccessorFn<number>;

  /** The column name that determines whether a link should have an arrow.
   * If provided, links will have arrows based on the `boolean` values in this column.
   */
  [BaseLinksConfigKeys.LinkArrow]?: string;

  /** Specifies the function that determines if a link should have an arrow based on the value in the `linkArrowBy` column.
   * It takes a link record as input and its index, and should return a boolean value.
   *
   * Works only when `linkArrowBy` is provided. Overrides the values in the `linkArrowBy` column by processing them (in this case the values in the `linkArrowBy` column can be of any type, not just booleans).
   *
   * @param {any} value - The value from the `LinkArrow` column.
   * @param {number} index - The index of the link.
   * @returns {boolean} A boolean indicating whether the link should have an arrow.
   */
  [BaseLinksConfigKeys.LinkArrowFn]?: AccessorFn<boolean>;

  /** The column name for the link strength.
   * If provided, links will have their strengths set based on the values in this column, which should be numeric values.
   * Link strength affects the force simulation.
   */
  [BaseLinksConfigKeys.LinkStrength]?: string;

  /** Specifies the function that will be used to generate the strength for each link based on the value in the `linkStrengthBy` column.
   * It takes a link record as input and its index, and should return a numeric value.
   *
   * Works only when `linkStrengthBy` is provided. Overrides the values in the `linkStrengthBy` column by processing them (in this case the values in the `linkStrengthBy` column can be of any type, not just numbers).
   *
   * @param {any} value - The value from the `LinkStrength` column.
   * @param {number} index - The index of the link.
   * @returns {number} The numeric strength value to be applied to the link.
   */
  [BaseLinksConfigKeys.LinkStrengthFn]?: AccessorFn<number>;


  /** Defines the range for automatic link strength scaling. Takes [min, max] values in the range [0, 1].
   *
   * This setting can be used to control the strength of the links during the simulation.
   *
   * Note: Only works when `linkStrength` column is provided and contains numeric values and when `linkStrengthFn` is not set. Has effect only during the active simulation.
   * @default [0.2, 1.0]
   */
  [BaseLinksConfigKeys.LinkStrengthRange]?: [number, number];

  /** An array of additional column names to include in the link data.
   *
   * These columns will be available on the link objects but not used by Cosmograph directly, can be used as accessors for Cosmograph components. Useful for storing additional information about the links.
   */
  [BaseLinksConfigKeys.LinkIncludeColumns]?: string[];
}


export interface CosmographClustersConfig {
  /** Array of cluster positions.
   *   - Every two elements represent the x and y coordinates for a cluster position.
   *   - `undefined` means the cluster's position is not defined and will use centermass positioning instead.
   * @example
   *   `[10, 20, 30, 40, undefined, undefined]` places the cluster 0 at (10, 20) and the cluster 1 at (30, 40);
   * the third cluster will be positioned at its centermass automatically.
   */
  [BaseClustersConfigKeys.ClusterPositions]?: (number | undefined)[];
}

export const pointsConfigKeys = Object.values(BasePointsConfigKeys) as Array<keyof CosmographConfig>
export const linksConfigKeys = Object.values(BaseLinksConfigKeys) as Array<keyof CosmographConfig>
export const clustersConfigKeys = Object.values(BaseClustersConfigKeys) as Array<keyof CosmographConfig>
export const requiredPointsConfigKeys = Object.values(RequiredPointsConfigKeys) as Array<keyof CosmographConfig>
export const requiredLinksConfigKeys = Object.values(RequiredLinksConfigKeys) as Array<keyof CosmographConfig>

export const requiredProps = [
  ...requiredPointsConfigKeys,
  ...requiredLinksConfigKeys,
  BasePointsConfigKeys.PointIncludeColumns, // not required but needed to generate points data
  BaseLinksConfigKeys.LinkIncludeColumns, // not required but needed to generate links data
]

export const unrequiredProps: (keyof CosmographConfig)[] = [
  ...pointsConfigKeys,
  ...linksConfigKeys,
  ...clustersConfigKeys,
].filter( // remove PointIncludeColumns/LinkIncludeColumns as they don't participate in rendering/setting cosmos buffers
  key =>
    key !== BasePointsConfigKeys.PointIncludeColumns &&
    key !== BaseLinksConfigKeys.LinkIncludeColumns
)

/**
 * Represents the input data for Cosmograph points or links.
 * This can be an array of objects or a more complex data structure
 * containing information about each point or link to be rendered. Accepts `File`, `string`, `Table` (Apache Arrow), `Uint8Array` (Apache Arrow), `ArrayBuffer` (Apache Arrow), `Record<string, unknown>[]`
 *
 * `string` will be treated as table name in external DuckDB-Wasm instance when `duckDbConnection` is provided to the Cosmograph instance.
 */
export type CosmographInputData = File | string | Table | Uint8Array | ArrayBuffer | Record<string, unknown>[]

export type AccessorFn<T> =
  ((value: number, i?: number) => T) |
  ((value: string, i?: number) => T) |
  ((value: boolean, i?: number) => T) |
  ((value: Date, i?: number) => T)

export interface CosmographDataConfig extends CosmographPointsConfig, CosmographLinksConfig, CosmographClustersConfig {}

