import { GraphConfigInterface } from '@cosmograph/cosmos'

export interface SimulationConfig {
  /**
   * Decay coefficient. Use bigger values if you want the simulation to "cool down" slower.
   * Default value: `5000`
   */
  simulationDecay?: GraphConfigInterface['simulationDecay'];

  /**
   * Gravity force coefficient.
   * Default value: `0.25`
   */
  simulationGravity?: GraphConfigInterface['simulationGravity'];

  /**
   * Centering to center mass force coefficient.
   * Default value: `0`
   */
  simulationCenter?: GraphConfigInterface['simulationCenter'];

  /**
   * Repulsion force coefficient.
   * Default value: `1.0`
   */
  simulationRepulsion?: GraphConfigInterface['simulationRepulsion'];

  /**
   * Decreases / increases the detalization of the Many-Body force calculations.
   * When `useQuadtree` is set to `true`, this property corresponds to the Barnes–Hut approximation criterion.
   * Default value: `1.15`
   */
  simulationRepulsionTheta?: GraphConfigInterface['simulationRepulsionTheta'];

  /**
   * Barnes–Hut approximation depth.
   * Can only be used when `useQuadtree` is set `true`.
   * Default value: `12`
   */
  simulationRepulsionQuadtreeLevels?: GraphConfigInterface['simulationRepulsionQuadtreeLevels'];

  /**
   * Link spring force coefficient.
   * Default value: `1`
   */
  simulationLinkSpring?: GraphConfigInterface['simulationLinkSpring'];

  /**
   * Minimum link distance.
   * Default value: `10`
   */
  simulationLinkDistance?: GraphConfigInterface['simulationLinkDistance'];

  /**
   * Range of random link distance values.
   * Default value: `[1, 1.2]`
   */
  simulationLinkDistRandomVariationRange?: GraphConfigInterface['simulationLinkDistRandomVariationRange'];

  /**
   * Repulsion coefficient from mouse position.
   * The repulsion force is activated by pressing the right mouse button.
   * Default value: `2`
   */
  simulationRepulsionFromMouse?: GraphConfigInterface['simulationRepulsionFromMouse'];

  /**
   * Friction coefficient.
   * Default value: `0.85`
   */
  simulationFriction?: GraphConfigInterface['simulationFriction'];

  /**
   * Coefficient of simulation cluster strength from 0 to 1. If `undefined`, the graph will not apply any clustering force to the simulation.
   * Default value: `undefined`.
   */
  simulationClusterStrength?: GraphConfigInterface['simulationCluster'];


  /**
   * Coefficient of simulation impulse from 0 to 1. If `undefined`, the graph will not apply a new impulse to the renders during adding or removing data items, but instead will use the current simulation impulse value from the graph. Has no impact if `disableSimulation` is `true`.
   * Default value: `undefined`.
   */
  simulationImpulse?: number;
}

export interface SimulationEventConfig {
  /** Callback function that will be called when the simulation starts. */
  onSimulationStart?: GraphConfigInterface['onSimulationStart'];

  /** Callback function that will be called when the simulation stops. */
  onSimulationEnd?: GraphConfigInterface['onSimulationEnd'];

  /** Callback function that will be called when the simulation is paused. */
  onSimulationPause?: GraphConfigInterface['onSimulationPause'];

  /** Callback function that will be called when the simulation is restarted. */
  onSimulationRestart?: GraphConfigInterface['onSimulationRestart'];

  /** Callback function that will be called on every tick of the simulation.
   *
   * @param {number} alpha - The value of alpha will decrease over time as the simulation "cools down".
   * @param {number | undefined} index - The index of the hovered point.
   * @param {[number, number] | undefined} pointPosition - The position of the point under the cursor.
   */
  onSimulationTick?: GraphConfigInterface['onSimulationTick'];
}
